namespace Lof\SmsNotification\Model;

class Phone extends \Magento\Framework\Model\AbstractModel
{
      protected $blacklist;
   /**
     * @param \Magento\Framework\Model\Context $context
     * @param \Magento\Framework\Registry $registry
     * @param \Magento\Framework\Model\ResourceModel\AbstractResource|null $resource
     * @param \Magento\Framework\Data\Collection\AbstractDb|null $resourceCollection
     * @param \Magento\Framework\UrlInterface $urlBuilder
     * @param array $data
     */
    function __construct(
        \Magento\Framework\Model\Context $context,
        \Magento\Framework\Registry $registry,
        \Lof\SmsNotification\Model\Blacklist $blacklist,
        \Magento\Framework\Model\ResourceModel\AbstractResource $resource = null,
        \Magento\Framework\Data\Collection\AbstractDb $resourceCollection = null,
        array $data = [])
    {
        $this->blacklist = $blacklist;
        parent::__construct($context, $registry, $resource, $resourceCollection, $data);
    }

    /**
     * @return void
     */
    protected function _construct()
    {
        $this->_init('Lof\SmsNotification\Model\ResourceModel\Phone');
    }
     public function isBlacklist($mobile) {
       
        foreach ($this->blacklist->getCollection() as $key => $_blacklist) {
            if($_blacklist->getSms() == $mobile) {
                return  true;
            }
        }
    }

    
}
