namespace Magebest\Brand\Controller\Adminhtml\Group;

class Index extends \Magebest\Brand\Controller\Adminhtml\Group
{
	/**
     * Check the permission to run it
     *
     * @return bool
     */
    protected function _isAllowed()
    {
        return $this->_authorization->isAllowed('Magebest_Brand::group');
    }

	/**
	 * Brand list action
	 *
	 * @return \Magento\Backend\Model\View\Result\Page|\Magento\Backend\Model\View\Result\Forward
	 */
	public function execute()
	{

		$resultPage = $this->resultPageFactory->create();

		/**
		 * Set active menu item
		 */
		$resultPage->setActiveMenu("Magebest_Brand::brand");
		$resultPage->getConfig()->getTitle()->prepend(__('Brand Groups'));

		/**
		 * Add breadcrumb item
		 */
		$resultPage->addBreadcrumb(__('Brands'),__('Brands'));
		$resultPage->addBreadcrumb(__('Manage Brands'),__('Manage Brand Groups'));

		return $resultPage;
	}
}